import { getRoleList as _getRoleList, getAllPermissionList, addRole, editRole, deleteRole, setDefaultRole, editRolePerm, initDepartment, loadDepartment, editRoleDep } from "@/api/index";
import util from "@/libs/util.js";
import circleLoading from "../../my-components/circle-loading.vue";
export default {
  name: "role-manage",
  components: {
    circleLoading: circleLoading
  },
  data: function data() {
    var _this = this;

    return {
      loading: true,
      treeLoading: true,
      depTreeLoading: true,
      operationLoading: false,
      submitPermLoading: false,
      submitDepLoading: false,
      searchKey: "",
      sortColumn: "createTime",
      sortType: "desc",
      modalType: 0,
      roleModalVisible: false,
      permModalVisible: false,
      depModalVisible: false,
      modalTitle: "",
      roleForm: {
        description: ""
      },
      roleFormValidate: {
        name: [{
          required: true,
          message: "角色名称不能为空",
          trigger: "blur"
        }]
      },
      submitLoading: false,
      selectList: [],
      selectCount: 0,
      columns: [{
        type: "selection",
        width: 60,
        align: "center"
      }, {
        type: "index",
        width: 60,
        align: "center"
      }, {
        title: "角色名称",
        key: "name",
        width: 150,
        sortable: true
      }, {
        title: "备注",
        key: "description",
        width: 190,
        sortable: true
      }, {
        title: "创建时间",
        key: "createTime",
        width: 160,
        sortable: true,
        sortType: "desc"
      }, {
        title: "更新时间",
        key: "updateTime",
        width: 160,
        sortable: true
      }, {
        title: "是否设置为注册用户默认角色",
        key: "defaultRole",
        align: "center",
        width: 180,
        render: function render(h, params) {
          if (params.row.defaultRole) {
            return h("div", [h("Button", {
              props: {
                type: "success",
                size: "small"
              },
              style: {
                marginRight: "5px"
              },
              on: {
                click: function click() {
                  _this.cancelDefault(params.row);
                }
              }
            }, "取消默认")]);
          } else {
            return h("div", [h("Button", {
              props: {
                type: "info",
                size: "small"
              },
              style: {
                marginRight: "5px"
              },
              on: {
                click: function click() {
                  _this.setDefault(params.row);
                }
              }
            }, "设为默认")]);
          }
        }
      }, {
        title: "操作",
        key: "action",
        align: "center",
        width: 300,
        render: function render(h, params) {
          return h("div", [h("Button", {
            props: {
              type: "warning",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.editPerm(params.row);
              }
            }
          }, "菜单权限"), h("Button", {
            props: {
              type: "primary",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.editDep(params.row);
              }
            }
          }, "数据权限"), h("Button", {
            props: {
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.edit(params.row);
              }
            }
          }, "编辑"), h("Button", {
            props: {
              type: "error",
              size: "small"
            },
            on: {
              click: function click() {
                _this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      pageNumber: 1,
      pageSize: 10,
      total: 0,
      permData: [],
      editRolePermId: "",
      selectAllFlag: false,
      depData: [],
      dataType: 0,
      editDepartments: []
    };
  },
  methods: {
    init: function init() {
      this.getRoleList(); // 获取所有菜单权限树

      this.getPermList();
    },
    changePage: function changePage(v) {
      this.pageNumber = v;
      this.getRoleList();
      this.clearSelectAll();
    },
    changePageSize: function changePageSize(v) {
      this.pageSize = v;
      this.getRoleList();
    },
    changeSort: function changeSort(e) {
      this.sortColumn = e.key;
      this.sortType = e.order;

      if (e.order === "normal") {
        this.sortType = "";
      }

      this.getRoleList();
    },
    getRoleList: function getRoleList() {
      var _this2 = this;

      this.loading = true;
      var params = {
        pageNumber: this.pageNumber,
        pageSize: this.pageSize,
        sort: this.sortColumn,
        order: this.sort
      };

      _getRoleList(params).then(function (res) {
        _this2.loading = false;

        if (res.success === true) {
          _this2.data = res.result.content;
          _this2.total = res.result.totalElements;
        }
      });
    },
    getPermList: function getPermList() {
      var _this3 = this;

      this.treeLoading = true;
      getAllPermissionList().then(function (res) {
        _this3.treeLoading = false;

        if (res.success === true) {
          _this3.deleteDisableNode(res.result);

          _this3.permData = res.result;
        }
      });
    },
    // 递归标记禁用节点
    deleteDisableNode: function deleteDisableNode(permData) {
      var that = this;
      permData.forEach(function (e) {
        if (e.status === -1) {
          e.title = "[已禁用] " + e.title;
          e.disabled = true;
        }

        if (e.children && e.children.length > 0) {
          that.deleteDisableNode(e.children);
        }
      });
    },
    cancelRole: function cancelRole() {
      this.roleModalVisible = false;
    },
    submitRole: function submitRole() {
      var _this4 = this;

      this.$refs.roleForm.validate(function (valid) {
        if (valid) {
          if (_this4.modalType === 0) {
            // 添加
            _this4.submitLoading = true;
            addRole(_this4.roleForm).then(function (res) {
              _this4.submitLoading = false;

              if (res.success === true) {
                _this4.$Message.success("操作成功");

                _this4.getRoleList();

                _this4.roleModalVisible = false;
              }
            });
          } else {
            _this4.submitLoading = true;
            editRole(_this4.roleForm).then(function (res) {
              _this4.submitLoading = false;

              if (res.success === true) {
                _this4.$Message.success("操作成功");

                _this4.getRoleList();

                _this4.roleModalVisible = false;
              }
            });
          }
        }
      });
    },
    addRole: function addRole() {
      this.modalType = 0;
      this.modalTitle = "添加角色";
      this.$refs.roleForm.resetFields();
      delete this.roleForm.id;
      this.roleModalVisible = true;
    },
    edit: function edit(v) {
      this.modalType = 1;
      this.modalTitle = "编辑角色"; // 转换null为""

      for (var attr in v) {
        if (v[attr] === null) {
          v[attr] = "";
        }
      }

      var str = JSON.stringify(v);
      var roleInfo = JSON.parse(str);
      this.roleForm = roleInfo;
      this.roleModalVisible = true;
    },
    remove: function remove(v) {
      var _this5 = this;

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除角色 " + v.name + " ?",
        onOk: function onOk() {
          _this5.operationLoading = true;
          deleteRole(v.id).then(function (res) {
            _this5.operationLoading = false;

            if (res.success === true) {
              _this5.$Message.success("删除成功");

              _this5.getRoleList();
            }
          });
        }
      });
    },
    setDefault: function setDefault(v) {
      var _this6 = this;

      this.$Modal.confirm({
        title: "确认设置",
        content: "您确认要设置所选的 " + v.name + " 为注册用户默认角色?",
        onOk: function onOk() {
          var params = {
            id: v.id,
            isDefault: true
          };
          _this6.operationLoading = true;
          setDefaultRole(params).then(function (res) {
            _this6.operationLoading = false;

            if (res.success === true) {
              _this6.$Message.success("操作成功");

              _this6.getRoleList();
            }
          });
        }
      });
    },
    cancelDefault: function cancelDefault(v) {
      var _this7 = this;

      this.$Modal.confirm({
        title: "确认取消",
        content: "您确认要取消所选的 " + v.name + " 角色为默认?",
        onOk: function onOk() {
          var params = {
            id: v.id,
            isDefault: false
          };
          _this7.operationLoading = true;
          setDefaultRole(params).then(function (res) {
            _this7.operationLoading = false;

            if (res.success === true) {
              _this7.$Message.success("操作成功");

              _this7.getRoleList();
            }
          });
        }
      });
    },
    clearSelectAll: function clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    changeSelect: function changeSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    delAll: function delAll() {
      var _this8 = this;

      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        onOk: function onOk() {
          var ids = "";

          _this8.selectList.forEach(function (e) {
            ids += e.id + ",";
          });

          ids = ids.substring(0, ids.length - 1);
          _this8.operationLoading = true;
          deleteRole(ids).then(function (res) {
            _this8.operationLoading = false;

            if (res.success === true) {
              _this8.$Message.success("删除成功");

              _this8.clearSelectAll();

              _this8.getRoleList();
            }
          });
        }
      });
    },
    editPerm: function editPerm(v) {
      this.editRolePermId = v.id;
      this.modalTitle = "分配 " + v.name + " 的菜单权限(点击选择)"; // 匹配勾选

      var rolePerms = v.permissions; // 递归判断子节点

      this.checkPermTree(this.permData, rolePerms);
      this.permModalVisible = true;
    },
    // 递归判断子节点
    checkPermTree: function checkPermTree(permData, rolePerms) {
      var that = this;
      permData.forEach(function (p) {
        if (that.hasPerm(p, rolePerms)) {
          p.selected = true;
        } else {
          p.selected = false;
        }

        if (p.children && p.children.length > 0) {
          that.checkPermTree(p.children, rolePerms);
        }
      });
    },
    // 判断角色拥有的权限节点勾选
    hasPerm: function hasPerm(p, rolePerms) {
      var flag = false;

      for (var i = 0; i < rolePerms.length; i++) {
        if (p.id === rolePerms[i].permissionId) {
          flag = true;
          break;
        }
      }

      if (flag) {
        return true;
      }

      return false;
    },
    // 全选反选
    selectTreeAll: function selectTreeAll() {
      this.selectAllFlag = !this.selectAllFlag;
      var select = this.selectAllFlag;
      this.selectedTreeAll(this.permData, select);
    },
    // 递归全选节点
    selectedTreeAll: function selectedTreeAll(permData, select) {
      var that = this;
      permData.forEach(function (e) {
        e.selected = select;

        if (e.children && e.children.length > 0) {
          that.selectedTreeAll(e.children, select);
        }
      });
    },
    submitPermEdit: function submitPermEdit() {
      var _this9 = this;

      this.submitPermLoading = true;
      var permIds = "";
      var selectedNodes = this.$refs.tree.getSelectedNodes();
      selectedNodes.forEach(function (e) {
        permIds += e.id + ",";
      });
      permIds = permIds.substring(0, permIds.length - 1);
      editRolePerm({
        roleId: this.editRolePermId,
        permIds: permIds
      }).then(function (res) {
        _this9.submitPermLoading = false;

        if (res.success === true) {
          _this9.$Message.success("操作成功");

          util.initRouter(_this9);

          _this9.getRoleList();

          _this9.permModalVisible = false;
        }
      });
    },
    cancelPermEdit: function cancelPermEdit() {
      this.permModalVisible = false;
    },
    loadData: function loadData(item, callback) {
      loadDepartment(item.id, {
        openDataFilter: false
      }).then(function (res) {
        if (res.success === true) {
          res.result.forEach(function (e) {
            e.selected = false;

            if (e.isParent) {
              e.loading = false;
              e.children = [];
            }

            if (e.status === -1) {
              e.title = "[已禁用] " + e.title;
              e.disabled = true;
            }
          });
          callback(res.result);
        }
      });
    },
    editDep: function editDep(v) {
      var _this10 = this;

      this.dataType = 0;
      this.editRolePermId = v.id;
      this.modalTitle = "分配 " + v.name + " 的数据权限";

      if (v.dataType) {
        this.dataType = v.dataType;
      } // 匹配勾选


      var roleDepIds = v.departments;
      this.editDepartments = roleDepIds;
      this.depTreeLoading = true;
      initDepartment({
        openDataFilter: false
      }).then(function (res) {
        _this10.depTreeLoading = false;

        if (res.success === true) {
          res.result.forEach(function (e) {
            e.selected = false;

            if (e.isParent) {
              e.loading = false;
              e.children = [];
            }

            if (e.status === -1) {
              e.title = "[已禁用] " + e.title;
              e.disabled = true;
            }
          });
          _this10.depData = res.result; // 判断子节点

          _this10.checkDepTree(_this10.depData, roleDepIds);

          _this10.depModalVisible = true;
        }
      });
    },
    expandCheckDep: function expandCheckDep(v) {
      // 判断展开子节点
      this.checkDepTree(v.children, this.editDepartments);
    },
    // 判断子节点
    checkDepTree: function checkDepTree(depData, roleDepIds) {
      var that = this;
      depData.forEach(function (p) {
        if (that.hasDepPerm(p, roleDepIds)) {
          p.selected = true;
        } else {
          p.selected = false;
        }
      });
    },
    // 判断角色拥有的权限节点勾选
    hasDepPerm: function hasDepPerm(p, roleDepIds) {
      var flag = false;

      for (var i = 0; i < roleDepIds.length; i++) {
        if (p.id === roleDepIds[i].departmentId) {
          flag = true;
          break;
        }
      }

      if (flag) {
        return true;
      }

      return false;
    },
    submitDepEdit: function submitDepEdit() {
      var _this11 = this;

      var depIds = "";

      if (this.dataType != 0) {
        var selectedNodes = this.$refs.depTree.getSelectedNodes();

        if (selectedNodes.length < 1) {
          this.$Message.error("请至少选择一条数据");
          return;
        }

        selectedNodes.forEach(function (e) {
          depIds += e.id + ",";
        });
        depIds = depIds.substring(0, depIds.length - 1);
      }

      this.submitDepLoading = true;
      editRoleDep({
        roleId: this.editRolePermId,
        dataType: this.dataType,
        depIds: depIds
      }).then(function (res) {
        _this11.submitDepLoading = false;

        if (res.success === true) {
          _this11.$Message.success("操作成功");

          _this11.getRoleList();

          _this11.depModalVisible = false;
        }
      });
    }
  },
  mounted: function mounted() {
    this.init();
  }
};
