import { checkOssSet, getFileListData, copyFile, renameFile, deleteFile, aliDownloadFile } from "@/api/index";
import circleLoading from "../../my-components/circle-loading.vue";
import "dplayer/dist/DPlayer.min.css";
import DPlayer from "dplayer";
export default {
  name: "oss-manage",
  components: {
    circleLoading: circleLoading
  },
  data: function data() {
    var _this = this;

    return {
      accessToken: {},
      // 上传token鉴权
      loading: false,
      // 表单加载状态
      operationLoading: false,
      // 操作加载状态
      fileType: "all",
      showType: "list",
      modalVisible: false,
      // 添加或编辑显示
      uploadVisible: false,
      videoVisible: false,
      picVisible: false,
      picTitle: "",
      videoTitle: "",
      modalTitle: "",
      // 添加或编辑标题
      searchForm: {
        // 搜索框对应data对象
        name: "",
        fkey: "",
        type: "",
        pageNumber: 1,
        // 当前页数
        pageSize: 5,
        // 页面大小
        sort: "createTime",
        // 默认排序字段
        order: "desc",
        // 默认排序方式
        startDate: "",
        // 起始时间
        endDate: "" // 终止时间

      },
      selectDate: null,
      // 选择日期绑定modal
      oldKey: "",
      form: {
        name: "",
        fkey: ""
      },
      file: {},
      // 表单验证规则
      formValidate: {
        name: [{
          required: true,
          message: "不能为空",
          trigger: "blur"
        }],
        fkey: [{
          required: true,
          message: "不能为空",
          trigger: "blur"
        }]
      },
      submitLoading: false,
      // 添加或编辑提交状态
      selectList: [],
      // 多选数据
      selectCount: 0,
      // 多选计数
      totalSize: "",
      // 文件大小统计
      columns: [// 表头
      {
        type: "selection",
        width: 60,
        align: "center"
      }, {
        type: "index",
        width: 60,
        align: "center"
      }, {
        title: "原文件名",
        key: "name",
        width: 130,
        sortable: true
      }, {
        title: "存储文件名",
        key: "fkey",
        width: 165,
        sortable: true
      }, {
        title: "缩略图(点击预览)",
        key: "url",
        width: 130,
        align: "center",
        render: function render(h, params) {
          if (params.row.type.includes("image") > 0) {
            return h("img", {
              attrs: {
                src: params.row.url,
                alt: "加载图片失败"
              },
              style: {
                cursor: "pointer",
                width: "80px",
                height: "60px",
                margin: "10px 0",
                "object-fit": "contain"
              },
              on: {
                click: function click() {
                  _this.showPic(params.row);
                }
              }
            });
          } else if (params.row.type.includes("video") > 0) {
            return h("video", {
              style: {
                cursor: "pointer",
                width: "80px",
                height: "60px",
                margin: "10px 0",
                "object-fit": "contain"
              },
              on: {
                click: function click() {
                  _this.showVideo(params.row);
                }
              }
            }, [h("source", {
              attrs: {
                src: params.row.url
              }
            })]);
          } else {
            return h("span", "非多媒体类型");
          }
        }
      }, {
        title: "文件类型",
        key: "type",
        width: 110,
        sortable: true
      }, {
        title: "文件大小",
        key: "size",
        width: 110,
        sortable: true,
        render: function render(h, params) {
          var m = (params.row.size * 1.0 / (1024 * 1024)).toFixed(2) + " MB";
          return h("span", m);
        }
      }, {
        title: "上传者",
        key: "createBy",
        width: 120,
        sortable: true
      }, {
        title: "存储位置",
        key: "location",
        align: "center",
        width: 110,
        render: function render(h, params) {
          if (params.row.location === 0) {
            return h("div", [h("Tag", {
              props: {
                color: "green"
              }
            }, "本地服务器")]);
          } else if (params.row.location === 1) {
            return h("div", [h("Tag", {
              props: {
                color: "blue"
              }
            }, "七牛云")]);
          } else if (params.row.location === 2) {
            return h("div", [h("Tag", {
              props: {
                color: "orange"
              }
            }, "阿里云")]);
          } else if (params.row.location === 3) {
            return h("div", [h("Tag", {
              props: {
                color: "geekblue"
              }
            }, "腾讯云")]);
          }
        }
      }, {
        title: "创建时间",
        key: "createTime",
        width: 150,
        sortable: true,
        sortType: "desc"
      }, {
        title: "操作",
        key: "action",
        align: "center",
        fixed: "right",
        width: 235,
        render: function render(h, params) {
          return h("div", [h("Button", {
            props: {
              type: "primary",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.download(params.row);
              }
            }
          }, "下载"), h("Button", {
            props: {
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.rename(params.row);
              }
            }
          }, "重命名"), h("Button", {
            props: {
              type: "warning",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.copy(params.row);
              }
            }
          }, "复制"), h("Button", {
            props: {
              type: "error",
              size: "small"
            },
            on: {
              click: function click() {
                _this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      // 表单数据
      total: 0,
      // 表单数据总数
      pageSizeOpts: [5, 10, 20]
    };
  },
  methods: {
    init: function init() {
      var _this2 = this;

      this.accessToken = {
        accessToken: this.getStore("accessToken")
      };
      checkOssSet().then(function (res) {
        if (!res.success) {
          _this2.$Modal.confirm({
            title: "您还未配置OSS",
            content: "您还未配置第三方OSS服务，是否现在立即去配置?",
            onOk: function onOk() {
              _this2.$router.push({
                name: "setting",
                query: {
                  name: "oss"
                }
              });
            }
          });
        } else {
          _this2.getDataList();
        }
      });
    },
    showPic: function showPic(v) {
      this.file = v;
      this.file.msize = (v.size * 1.0 / (1024 * 1024)).toFixed(2) + " MB";
      this.picTitle = v.name + "(" + v.fkey + ")";
      this.picVisible = true;
    },
    showVideo: function showVideo(v) {
      var dp = new DPlayer({
        container: document.getElementById("dplayer"),
        screenshot: true,
        video: {
          url: v.url
        },
        danmaku: {
          id: v.fkey,
          api: "https://api.prprpr.me/dplayer/"
        }
      });
      this.file = v;
      this.file.msize = (v.size * 1.0 / (1024 * 1024)).toFixed(2) + " MB";
      this.videoTitle = v.name + "(" + v.fkey + ")";
      this.videoVisible = true;
    },
    changePage: function changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize: function changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    changeSort: function changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;

      if (e.order === "normal") {
        this.searchForm.order = "";
      }

      this.getDataList();
    },
    selectDateRange: function selectDateRange(v) {
      if (v) {
        this.searchForm.startDate = v[0];
        this.searchForm.endDate = v[1];
      }
    },
    changeShowType: function changeShowType() {
      this.searchForm.pageNumber = 1;

      if (this.showType == "list") {
        this.searchForm.pageSize = 5;
      } else {
        this.searchForm.pageSize = 12;
      }

      this.getDataList();
    },
    getDataList: function getDataList() {
      var _this3 = this;

      if (this.showType == "list") {
        this.pageSizeOpts = [5, 10, 20];
      } else {
        this.pageSizeOpts = [12, 24];
      }

      this.loading = true;
      getFileListData(this.searchForm).then(function (res) {
        _this3.loading = false;

        if (res.success) {
          _this3.data = res.result.content;
          _this3.total = res.result.totalElements;
        }
      });
    },
    handleSearch: function handleSearch() {
      this.searchForm.pageNumber = 1;

      if (this.showType == "list") {
        this.searchForm.pageSize = 5;
      } else {
        this.searchForm.pageSize = 12;
      }

      this.getDataList();
    },
    changeFileType: function changeFileType() {
      var name = this.fileType;

      if (name == "all") {
        this.searchForm.type = "";
      } else if (name == "pic") {
        this.searchForm.type = "image";
      } else if (name == "video") {
        this.searchForm.type = "video";
      }

      this.handleSearch();
    },
    handleReset: function handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;

      if (this.showType == "list") {
        this.searchForm.pageSize = 5;
      } else {
        this.searchForm.pageSize = 12;
      }

      this.selectDate = null;
      this.searchForm.startDate = "";
      this.searchForm.endDate = ""; // 重新加载数据

      this.getDataList();
    },
    beforeUpload: function beforeUpload() {
      if (!this.$route.meta.permTypes.includes("upload")) {
        this.$Message.error("此处您没有上传权限(为演示功能，该按钮未配置隐藏)");
        return false;
      }

      return true;
    },
    handleMaxSize: function handleMaxSize(file) {
      this.$Notice.warning({
        title: "文件大小过大",
        desc: "所选文件‘ " + file.name + " ’大小过大, 不得超过 5M."
      });
    },
    handleSuccess: function handleSuccess(res, file) {
      if (res.success === true) {
        this.$Message.success("上传文件 " + file.name + " 成功");
        this.getDataList();
      } else {
        this.$Message.error(res.message);
      }
    },
    handleError: function handleError(error, file, fileList) {
      this.$Message.error(error.toString());
    },
    clearFiles: function clearFiles() {
      this.$refs.up.clearFiles();
    },
    handleCancel: function handleCancel() {
      this.modalVisible = false;
    },
    download: function download(v) {
      window.open(v.url + "?attname=&response-content-type=application/octet-stream");
    },
    copy: function copy(v) {
      var _this4 = this;

      this.$Modal.confirm({
        title: "确认复制",
        content: "您确认要复制文件 " + v.name + " ?",
        onOk: function onOk() {
          _this4.operationLoading = true;
          copyFile({
            id: v.id,
            key: v.fkey
          }).then(function (res) {
            _this4.operationLoading = false;

            if (res.success) {
              _this4.$Message.success("复制文件成功，新文件名为 " + v.name + "_副本");

              _this4.getDataList();
            }
          });
        }
      });
    },
    removeAll: function removeAll() {
      var _this5 = this;

      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 个文件?",
        onOk: function onOk() {
          var ids = "";

          _this5.selectList.forEach(function (e) {
            ids += e.id + ",";
          });

          ids = ids.substring(0, ids.length - 1);
          _this5.operationLoading = true;
          deleteFile(ids).then(function (res) {
            _this5.operationLoading = false;

            if (res.success) {
              _this5.$Message.success("批量删除文件成功");

              _this5.clearSelectAll();

              _this5.getDataList();
            }
          });
        }
      });
    },
    remove: function remove(v) {
      var _this6 = this;

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除文件 " + v.name + " ?",
        onOk: function onOk() {
          _this6.operationLoading = true;
          deleteFile(v.id).then(function (res) {
            _this6.operationLoading = false;

            if (res.success) {
              _this6.$Message.success("删除文件 " + v.name + " 成功");

              _this6.operationLoading = false;

              _this6.getDataList();
            }
          });
        }
      });
    },
    handleSubmit: function handleSubmit() {
      var _this7 = this;

      this.$refs.form.validate(function (valid) {
        if (valid) {
          _this7.submitLoading = true;
          var params = {
            id: _this7.form.id,
            key: _this7.oldKey,
            newKey: _this7.form.fkey,
            newName: _this7.form.name
          };
          renameFile(params).then(function (res) {
            _this7.submitLoading = false;

            if (res.success === true) {
              _this7.$Message.success("操作成功");

              _this7.getDataList();

              _this7.modalVisible = false;
            }
          });
        }
      });
    },
    rename: function rename(v) {
      this.modalTitle = "编辑文件名"; // 转换null为""

      for (var attr in v) {
        if (v[attr] === null) {
          v[attr] = "";
        }
      }

      var str = JSON.stringify(v);
      var data = JSON.parse(str);
      this.form = data;
      this.oldKey = data.fkey;
      this.modalVisible = true;
    },
    clearSelectAll: function clearSelectAll() {
      this.$refs.table.selectAll(false);
      this.totalSize = "";
    },
    changeSelect: function changeSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
      var size = 0;
      e.forEach(function (item) {
        size += item.size;
      });
      this.totalSize = (size * 1.0 / (1024 * 1024)).toFixed(2) + " MB";
    }
  },
  mounted: function mounted() {
    this.init();
  }
};
