import { doneList, updateInsStatus, deleteHistoricTask } from "@/api/activiti";
import { getDictDataByType } from "@/api/index";
import circleLoading from "../../my-components/circle-loading.vue";
import util from "@/libs/util";
export default {
  name: "done-manage",
  components: {
    circleLoading: circleLoading
  },
  data: function data() {
    var _this = this;

    return {
      loading: true,
      // 表单加载状态
      operationLoading: false,
      // 操作加载状态
      selectCount: 0,
      // 多选计数
      selectList: [],
      // 多选数据
      searchForm: {
        // 搜索框对应data对象
        name: "",
        pageNumber: 1,
        // 当前页数
        pageSize: 10 // 页面大小

      },
      modalType: 0,
      // 添加或编辑标识
      modalVisible: false,
      // 添加或编辑显示
      modalTitle: "",
      // 添加或编辑标题
      form: {
        // 添加或编辑表单对象初始化数据
        reason: ""
      },
      formValidate: {// 表单验证规则
      },
      submitLoading: false,
      // 添加或编辑提交状态
      columns: [// 表头
      {
        type: "selection",
        width: 60,
        align: "center"
      }, {
        type: "index",
        width: 60,
        align: "center"
      }, {
        title: "任务名称",
        key: "name",
        width: 160
      }, {
        title: "所属流程",
        key: "processName",
        width: 150
      }, {
        title: "委托代办人",
        key: "owner",
        width: 130
      }, {
        title: "流程发起人",
        key: "applyer",
        width: 130
      }, {
        title: "优先级",
        key: "priority",
        width: 100,
        sortable: true,
        render: function render(h, params) {
          var text = "";

          _this.dictPriority.forEach(function (e) {
            if (e.value == params.row.priority) {
              text = e.title;
            }
          });

          return h("div", text);
        }
      }, {
        title: "审批操作",
        key: "deleteReason",
        align: "center",
        width: 130
      }, {
        title: "审批意见",
        key: "comment",
        align: "center",
        width: 130
      }, {
        title: "耗时",
        key: "duration",
        align: "center",
        width: 130,
        render: function render(h, params) {
          return h("div", util.millsToTime(params.row.duration));
        }
      }, {
        title: "创建时间",
        key: "createTime",
        width: 150,
        sortType: "desc",
        sortable: true
      }, {
        title: "操作",
        key: "action",
        align: "center",
        width: 200,
        fixed: "right",
        render: function render(h, params) {
          return h("div", [h("Button", {
            props: {
              type: "primary",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.detail(params.row);
              }
            }
          }, "表单数据"), h("Button", {
            props: {
              type: "error",
              size: "small"
            },
            on: {
              click: function click() {
                _this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      // 表单数据
      total: 0,
      // 表单数据总数
      deleteId: "",
      dictPriority: []
    };
  },
  methods: {
    init: function init() {
      this.getDictDataType();
      this.getDataList();
    },
    getDictDataType: function getDictDataType() {
      var _this2 = this;

      getDictDataByType("priority").then(function (res) {
        if (res.success) {
          _this2.dictPriority = res.result;
        }
      });
    },
    changePage: function changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize: function changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    getDataList: function getDataList() {
      var _this3 = this;

      this.loading = true;
      doneList(this.searchForm).then(function (res) {
        _this3.loading = false;

        if (res.success === true) {
          _this3.data = res.result.content;
          _this3.total = res.result.totalElements;
        }
      });
    },
    handleSearch: function handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getDataList();
    },
    handleReset: function handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10; // 重新加载数据

      this.getDataList();
    },
    showSelect: function showSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    clearSelectAll: function clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    handelCancel: function handelCancel() {
      this.modalVisible = false;
    },
    handelSubmit: function handelSubmit() {
      var _this4 = this;

      this.$refs.form.validate(function (valid) {
        if (valid) {
          _this4.submitLoading = true;

          if (_this4.modalType === 0) {
            deleteProcessIns(_this4.deleteId).then(function (res) {
              _this4.submitLoading = false;

              if (res.success) {
                _this4.$Message.success("操作成功");

                _this4.modalVisible = false;

                _this4.getDataList();
              }
            });
          } else if (_this4.modalType === 1) {
            var ids = "";

            _this4.selectList.forEach(function (e) {
              ids += e.id + ",";
            });

            ids = ids.substring(0, ids.length - 1); // 批量删除

            deleteProcessIns(ids).then(function (res) {
              _this4.submitLoading = false;

              if (res.success) {
                _this4.$Message.success("操作成功");

                _this4.modalVisible = false;

                _this4.clearSelectAll();

                _this4.getDataList();
              }
            });
          }
        }
      });
    },
    editStatus: function editStatus(status, v) {
      var _this5 = this;

      var operation = "";

      if (status == 0) {
        operation = "暂停挂起";
      } else {
        operation = "激活运行";
      }

      this.$Modal.confirm({
        title: "确认" + operation,
        content: "\u60A8\u786E\u8BA4\u8981".concat(operation, "\u6D41\u7A0B\u5B9E\u4F8B").concat(v.name, "?"),
        onOk: function onOk() {
          _this5.operationLoading = true;
          var params = {
            status: status,
            id: v.id
          };
          updateInsStatus(params).then(function (res) {
            _this5.operationLoading = false;

            if (res.success === true) {
              _this5.$Message.success("操作成功");

              _this5.getDataList();
            }
          });
        }
      });
    },
    detail: function detail(v) {
      var query = {
        id: v.tableId,
        type: 3,
        backRoute: this.$route.name
      };
      this.$router.push({
        name: v.routeName,
        query: query
      });
    },
    edit: function edit(v) {},
    remove: function remove(v) {
      var _this6 = this;

      this.$Modal.confirm({
        title: "确认删除",
        // 记得确认修改此处
        content: "您确认要删除 " + v.name + " ?",
        onOk: function onOk() {
          _this6.operationLoading = true;
          deleteHistoricTask(v.id).then(function (res) {
            _this6.operationLoading = false;

            if (res.success === true) {
              _this6.$Message.success("操作成功");

              _this6.getDataList();
            }
          });
        }
      });
    },
    delAll: function delAll() {
      var _this7 = this;

      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }

      this.modalTitle = "确认要删除所选的 " + this.selectCount + " 条数据";
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        onOk: function onOk() {
          var ids = "";

          _this7.selectList.forEach(function (e) {
            ids += e.id + ",";
          });

          ids = ids.substring(0, ids.length - 1); // 批量删除

          _this7.operationLoading = true;
          deleteHistoricTask(ids).then(function (res) {
            _this7.operationLoading = false;

            if (res.success === true) {
              _this7.$Message.success("操作成功");

              _this7.clearSelectAll();

              _this7.getDataList();
            }
          });
        }
      });
    }
  },
  mounted: function mounted() {
    this.init();
  },
  watch: {
    // 监听路由变化
    $route: function $route(to, from) {
      if (to.name == "done-manage") {
        this.getDataList();
      }
    }
  }
};
