    initOssSet: function initOssSet() {
      var _this3 = this;

      this.loading = true;
      checkOssSet().then(function (res) {
        if (res.success && res.result) {
          var oss = res.result;
          getOssSet(oss).then(function (res) {
            _this3.loading = false;

            if (res.result) {
              // 转换null为""
              for (var attr in res.result) {
                if (res.result[attr] === null) {
                  res.result[attr] = "";
                }
              }

              _this3.oss = res.result;
            } else {
              _this3.changedOssSK = true;
            }
          });
        } else {
          _this3.loading = false;
          _this3.changedOssSK = true;
        }
      });
    },
    changeOss: function changeOss(v) {
      var _this4 = this;

      if (v == "ALI_OSS") {
        this.endpointPH = "请输入EndPoint(地域节点) 非Bucket域名";
      } else if (v == "TENCENT_OSS") {
        this.endpointPH = "请输入完整请求域名";
      } else {
        this.endpointPH = "请输入空间访问域名";
      }

      getOssSet(v).then(function (res) {
        if (res.result) {
          // 转换null为""
          for (var attr in res.result) {
            if (res.result[attr] === null) {
              res.result[attr] = "";
            }
          }

          _this4.oss = res.result;
          _this4.changedOssSK = false;
        } else {
          _this4.oss = {
            serviceName: v
          };

          if (_this4.oss.serviceName == "LOCAL_OSS") {
            _this4.oss.http = "http://";
            _this4.oss.endpoint = "127.0.0.1:9999/xboot/file/view";
          }

          _this4.changedOssSK = true;
        }
      });
    },
