          for (var attr in res.result) {
            if (res.result[attr] === null) {
              res.result[attr] = "";
            }
          }

          _this5.sms = res.result;
          _this5.sms.type = "-1";

          _this5.changeSmsType(_this5.sms.type);
        } else {
          _this5.changedSmsSK = true;
        }
      });
    },
