            _this11.email.password = res.result;
            _this11.changedEmailPass = true;
          } else if (_this11.tabName == "vaptcha") {
            _this11.vaptcha.secretKey = res.result;
            _this11.changedVaptchaSK = true;
          }
        }
      });
    },
    saveEditOss: function saveEditOss() {
      var _this12 = this;

      if (this.oss.serviceName == "LOCAL_OSS") {
        if (!this.oss.http) {
          this.errorMsg = "请选择http协议";
          return;
        } else {
          this.errorMsg = "";
        }

        if (!this.oss.endpoint) {
          this.errorMsg = "请输入文件预览接口";
          return;
        } else {
          this.errorMsg = "";
        }

        if (!this.oss.filePath) {
          this.errorMsg2 = "请输入存储磁盘路径";
          return;
        } else {
          this.errorMsg2 = "";
        }

        editOssSet(this.oss).then(function (res) {
          _this12.saveLoading = false;

          if (res.success) {
            _this12.$Message.success("保存成功");
          }
        });
      } else {
        this.$refs.ossForm.validate(function (valid) {
          if (valid) {
            if (!_this12.oss.http) {
              _this12.errorMsg = "请选择http协议";
              return;
            } else {
              _this12.errorMsg = "";
            }

            _this12.saveLoading = true;
            _this12.oss.changed = _this12.changedOssSK;
            editOssSet(_this12.oss).then(function (res) {
              _this12.saveLoading = false;

              if (res.success) {
                _this12.$Message.success("保存成功");
              }
            });
          }
        });
      }
    },
