          });
        }
      });
    },
    saveEditCore: function saveEditCore() {
      var _this17 = this;

      this.$refs.coreForm.validate(function (valid) {
        if (valid) {
          _this17.saveLoading = true;
          editCoreSet(_this17.core).then(function (res) {
            _this17.saveLoading = false;

            if (res.success) {
              _this17.$Message.success("保存成功");
            }
          });
        }
      });
    }
  },
