import { userInfo, userInfoEdit, relatedInfo, unRelate, githubLogin, qqLogin, weiboLogin, sendCodeEmail, editEmail, sendSms, changeMobile, uploadFile, getDictDataByType } from "@/api/index";
import Cookies from "js-cookie";
export default {
  name: "ownspace_index",
  data: function data() {
    var validePhone = function validePhone(rule, value, callback) {
      var re = /^1[0-9]{10}$/;

      if (!re.test(value)) {
        callback(new Error("请输入正确格式的手机号"));
      } else {
        callback();
      }
    };

    return {
      accessToken: {},
      uploadFileUrl: uploadFile,
      userForm: {
        id: "",
        avatar: "",
        username: "",
        sex: "",
        mobile: "",
        email: "",
        status: "",
        type: "",
        typeTxt: "",
        address: "",
        addressArray: []
      },
      mobileEditForm: {
        mobile: "",
        code: ""
      },
      emailEditForm: {
        email: ""
      },
      defaultList: [{
        url: ""
      }],
      uploadList: [],
      viewImage: false,
      imgUrl: "",
      codeError: "",
      initPhone: "",
      initEmail: "",
      saveLoading: false,
      sended: false,
      count: 60,
      countButton: "60 s",
      sendedEmail: false,
      countEmail: 60,
      countButtonEmail: "60 s",
      savePassLoading: false,
      canGetIdentifyCode: false,
      // 是否可点获取验证码
      checkCodeLoading: false,
      checkPassLoading: false,
      sendingSms: false,
      mobileEditValidate: {
        mobile: [{
          required: true,
          message: "请输入手机号码"
        }, {
          validator: validePhone
        }]
      },
      emailEditValidate: {
        email: [{
          required: true,
          message: "请输入邮箱地址"
        }, {
          type: "email",
          message: "邮箱格式不正确"
        }]
      },
      editMobileVisible: false,
      // 显示填写验证码box
      gettingIdentifyCodeBtnContent: "获取验证码",
      // “获取验证码”按钮的文字
      editEmailVisible: false,
      sendEmailLoading: false,
      editEmailLoading: false,
      sendVerifyEmailLoading: false,
      canSendEditEmail: true,
      github: {
        related: false,
        id: "",
        username: ""
      },
      qq: {
        related: false,
        id: "",
        username: ""
      },
      weibo: {
        related: false,
        id: "",
        username: ""
      },
      jumping: false,
      dictSex: []
    };
  },
  methods: {
    init: function init() {
      var _this = this;

      this.getDictSexData();
      this.accessToken = {
        accessToken: this.getStore("accessToken")
      };
      var v = JSON.parse(Cookies.get("userInfo")); // 转换null为""

      for (var attr in v) {
        if (v[attr] === null) {
          v[attr] = "";
        }
      }

      var str = JSON.stringify(v);
      var userInfo = JSON.parse(str);
      userInfo.addressArray = [];
      this.userForm = userInfo;
      this.initPhone = userInfo.mobile;
      this.mobileEditForm.mobile = userInfo.mobile;
      this.initEmail = userInfo.email;
      this.emailEditForm.email = userInfo.email;
      this.defaultList[0].url = userInfo.avatar;

      if (userInfo.address !== null && userInfo.address !== "") {
        this.userForm.address = userInfo.address;
        this.userForm.addressArray = JSON.parse(userInfo.address);
      }

      if (this.userForm.type === 0) {
        this.userForm.typeTxt = "普通用户";
      } else if (this.userForm.type === 1) {
        this.userForm.typeTxt = "管理员";
      }

      relatedInfo(userInfo.username).then(function (res) {
        if (res.success) {
          var r = res.result;
          _this.github.related = r.github;
          _this.github.id = r.githubId;
          _this.github.username = r.githubUsername;
          _this.qq.related = r.qq;
          _this.qq.id = r.qqId;
          _this.qq.username = r.qqUsername;
          _this.weibo.related = r.weibo;
          _this.weibo.id = r.weiboId;
          _this.weibo.username = r.weiboUsername;
        } else {
          _this.$Message.error("加载绑定第三方账号信息失败");
        }
      });
    },
    getDictSexData: function getDictSexData() {
      var _this2 = this;

      // 获取性别字典数据
      getDictDataByType("sex").then(function (res) {
        if (res.success) {
          _this2.dictSex = res.result;
        }
      });
    },
    toRelateGithub: function toRelateGithub() {
      var _this3 = this;

      this.jumping = true;
      githubLogin().then(function (res) {
        if (res.success) {
          window.location.href = res.result;
        } else {
          _this3.jumping = false;
        }
      });
    },
    toRelateQQ: function toRelateQQ() {
      var _this4 = this;

      this.jumping = true;
      qqLogin().then(function (res) {
        if (res.success) {
          window.location.href = res.result;
        } else {
          _this4.jumping = false;
        }
      });
    },
    toRelateWeibo: function toRelateWeibo() {
      var _this5 = this;

      this.jumping = true;
      weiboLogin().then(function (res) {
        if (res.success) {
          window.location.href = res.result;
        } else {
          _this5.jumping = false;
        }
      });
    },
    unRelateGithub: function unRelateGithub() {
      var _this6 = this;

      this.$Modal.confirm({
        title: "确认解绑Github账号",
        content: "您确认要解除绑定 " + this.github.username + " ?",
        onOk: function onOk() {
          var params = {
            socialType: 0,
            ids: [_this6.github.id],
            usernames: _this6.userForm.username
          };
          unRelate(params).then(function (res) {
            if (res.success === true) {
              _this6.$Message.success("操作成功");

              _this6.github.related = false;
            }
          });
        }
      });
    },
    unRelateQQ: function unRelateQQ() {
      var _this7 = this;

      this.$Modal.confirm({
        title: "确认解绑QQ账号",
        content: "您确认要解除绑定 " + this.qq.username + " ?",
        onOk: function onOk() {
          var params = {
            socialType: 1,
            ids: [_this7.qq.id],
            usernames: _this7.userForm.username
          };
          unRelate(params).then(function (res) {
            if (res.success === true) {
              _this7.$Message.success("操作成功");

              _this7.qq.related = false;
            }
          });
        }
      });
    },
    unRelateWeibo: function unRelateWeibo() {
      var _this8 = this;

      this.$Modal.confirm({
        title: "确认解绑微博账号",
        content: "您确认要解除绑定 " + this.weibo.username + " ?",
        onOk: function onOk() {
          var params = {
            socialType: 2,
            ids: [_this8.weibo.id],
            usernames: _this8.userForm.username
          };
          unRelate(params).then(function (res) {
            if (res.success === true) {
              _this8.$Message.success("操作成功");

              _this8.weibo.related = false;
            }
          });
        }
      });
    },
    handleView: function handleView(imgUrl) {
      this.imgUrl = imgUrl;
      this.viewImage = true;
    },
    handleRemove: function handleRemove(file) {
      var fileList = this.$refs.upload.fileList;
      this.$refs.upload.fileList.splice(fileList.indexOf(file), 1);
    },
    handleSuccess: function handleSuccess(res, file) {
      if (res.success === true) {
        file.url = res.result;
        this.userForm.avatar = res.result;
        this.defaultList[0].url = res.result;
        this.$refs.upload.fileList.splice(0, 1);
      } else {
        this.$Message.error(res.message);
      }
    },
    handleError: function handleError(error, file, fileList) {
      this.$Message.error(error.toString());
    },
    handleFormatError: function handleFormatError(file) {
      this.$Notice.warning({
        title: "不支持的文件格式",
        desc: "所选文件‘ " + file.name + " ’格式不正确, 请选择 .jpg .jpeg .png .gif格式文件"
      });
    },
    handleMaxSize: function handleMaxSize(file) {
      this.$Notice.warning({
        title: "文件大小过大",
        desc: "所选文件‘ " + file.name + " ’大小过大, 不得超过 5M."
      });
    },
    handleBeforeUpload: function handleBeforeUpload() {
      var check = this.uploadList.length < 2;

      if (!check) {
        this.$Notice.warning({
          title: "最多只能上传 1 张图片"
        });
      }

      return check;
    },
    showChangeMobile: function showChangeMobile() {
      this.editMobileVisible = true;
    },
    showChangeEmail: function showChangeEmail() {
      this.editEmailVisible = true;
    },
    cancelEditUserInfo: function cancelEditUserInfo() {
      this.$store.commit("removeTag", "ownspace_old");
      localStorage.pageOpenedList = JSON.stringify(this.$store.state.app.pageOpenedList);
      var lastPageName = "";
      var length = this.$store.state.app.pageOpenedList.length;

      if (length > 1) {
        lastPageName = this.$store.state.app.pageOpenedList[length - 1].name;
      } else {
        lastPageName = this.$store.state.app.pageOpenedList[0].name;
      }

      this.$router.push({
        name: lastPageName
      });
    },
    saveEdit: function saveEdit() {
      var _this9 = this;

      this.saveLoading = true;
      var params = this.userForm;
      delete params.nickName;
      delete params.description;
      userInfoEdit(params).then(function (res) {
        _this9.saveLoading = false;

        if (res.success === true) {
          _this9.$Message.success("保存成功"); // 更新用户信息


          Cookies.set("userInfo", _this9.userForm); // 更新头像

          _this9.$store.commit("setAvatarPath", _this9.userForm.avatar);
        }
      });
    },
    changeAddress: function changeAddress() {
      this.userForm.address = JSON.stringify(this.userForm.addressArray);
    },
    cancelInputCodeBox: function cancelInputCodeBox() {
      this.editMobileVisible = false;
      this.userForm.mobile = this.initPhone;
    },
    cancelEditEmail: function cancelEditEmail() {
      this.editEmailVisible = false;
      this.emailEditForm.email = this.initEmail;
    },
    countDown: function countDown() {
      var that = this;

      if (this.count === 0) {
        this.sended = false;
        this.count = 60;
        return;
      } else {
        this.countButton = this.count + " s";
        this.count--;
      }

      setTimeout(function () {
        that.countDown();
      }, 1000);
    },
    countDownEmail: function countDownEmail() {
      var that = this;

      if (this.countEmail === 0) {
        this.sendedEmail = false;
        this.countEmail = 60;
        return;
      } else {
        this.countButtonEmail = this.countEmail + " s";
        this.countEmail--;
      }

      setTimeout(function () {
        that.countDownEmail();
      }, 1000);
    },
    getIdentifyCode: function getIdentifyCode() {
      var _this10 = this;

      this.$refs["mobileEditForm"].validate(function (valid) {
        if (valid) {
          _this10.sendingSms = true;
          sendSms(_this10.mobileEditForm.mobile).then(function (res) {
            _this10.sendingSms = false;

            if (res.success) {
              _this10.$Message.success("发送短信验证码成功"); // 开始倒计时


              _this10.sended = true;

              _this10.countDown();
            }
          });
        }
      });
    },
    submitEditMobile: function submitEditMobile() {
      var _this11 = this;

      this.$refs["mobileEditForm"].validate(function (valid) {
        if (valid) {
          if (!_this11.mobileEditForm.code) {
            _this11.codeError = "请填写短信验证码";
            return;
          } else {
            _this11.codeError = "";
          }

          _this11.checkCodeLoading = true;
          changeMobile(_this11.mobileEditForm).then(function (res) {
            _this11.checkCodeLoading = false;

            if (res.success) {
              _this11.$Message.success("修改成功");

              _this11.userForm.mobile = _this11.mobileEditForm.mobile;
              _this11.initPhone = _this11.mobileEditForm.mobile;

              _this11.updateUserInfo();

              _this11.editMobileVisible = false;
            }
          });
        }
      });
    },
    hasChangePhone: function hasChangePhone() {
      var _this12 = this;

      if (this.mobileEditForm.mobile === this.initPhone) {
        this.canGetIdentifyCode = true;
      } else {
        this.$refs["mobileEditForm"].validate(function (valid) {
          if (valid) {
            _this12.canGetIdentifyCode = false;
          } else {
            _this12.canGetIdentifyCode = true;
          }
        });
      }
    },
    hasChangeEmail: function hasChangeEmail() {
      if (this.emailEditForm.email === this.initEmail) {
        this.canSendEditEmail = true;
      } else {
        this.canSendEditEmail = false;
      }
    },
    sendVerifyEmail: function sendVerifyEmail() {
      var _this13 = this;

      this.$refs["emailEditForm"].validate(function (valid) {
        if (valid) {
          _this13.sendEmailLoading = true;
          sendCodeEmail(_this13.emailEditForm.email).then(function (res) {
            _this13.sendEmailLoading = false;

            if (res.success) {
              _this13.$Message.success("发送邮件验证码成功，请注意查收"); // 开始倒计时


              _this13.sendedEmail = true;

              _this13.countDownEmail();
            }
          });
        }
      });
    },
    submitEditEmail: function submitEditEmail() {
      var _this14 = this;

      this.$refs["emailEditForm"].validate(function (valid) {
        if (valid) {
          if (!_this14.emailEditForm.code) {
            _this14.codeError = "验证码不能为空";
            return;
          } else {
            _this14.codeError = "";
          }

          _this14.editEmailLoading = true;
          editEmail(_this14.emailEditForm).then(function (res) {
            _this14.editEmailLoading = false;

            if (res.success) {
              _this14.initEmail = _this14.emailEditForm.email;
              _this14.userForm.email = _this14.emailEditForm.email;

              _this14.updateUserInfo();

              _this14.$Message.success("修改邮件成功");

              _this14.editEmailVisible = false;
            }
          });
        }
      });
    },
    updateUserInfo: function updateUserInfo() {
      var _this15 = this;

      // 更新用户信息
      userInfo().then(function (res) {
        if (res.success) {
          // 避免超过大小限制
          delete res.result.permissions;

          if (_this15.getStore("saveLogin")) {
            // 保存7天
            Cookies.set("userInfo", JSON.stringify(res.result), {
              expires: 7
            });
          } else {
            Cookies.set("userInfo", JSON.stringify(res.result));
          }

          _this15.setStore("userInfo", res.result);
        }
      });
    }
  },
  mounted: function mounted() {
    this.uploadList = this.$refs.upload.fileList;
    this.init();
    this.hasChangePhone();
  }
};
